var account = {
    username: msg.payload.username,
    password: msg.payload.password
}
msg.payload = JSON.stringify(account)
return msg

if (msg.payload !== '')
    msg.payload = JSON.stringify({distance: msg.payload})
else msg.payload = 'Empty string'
return msg;

msg.payload = JSON.stringify({
    command: 'write',
    button: msg.topic,
    status: msg.payload
})
return msg;

if (msg.payload.PIR == 1) msg.payload = 'HIGH'
else msg.payload = 'LOW'
return msg;

if (msg.payload.US == 1) msg.payload = 'HIGH'
else msg.payload = 'LOW'
return msg;

const time_element = msg.payload.time.split("T")
const date = time_element[0]
const time = time_element[1].split("+")[0]
if (msg.payload.pir === 'null')
    msg.payload = {date: date, time: time, label: 'Obstacles'}
else if (msg.payload.us === 'null')
    msg.payload = {date: date, time: time, label: 'People'}

return msg;

var cur_date = new Date()
var start_date = new Date("2022/07/15");  // some date
var diff_ms = Math.abs(cur_date - start_date);  // difference in milliseconds
var diff_days = Math.round(diff_ms / 86400000)
msg.payload = diff_days.toString() + ' days'
return msg;
